/**
* \brief free the CM diff_list after serialization // SPECIAL CASE: e.g. CL_ALARM_RSP
*
* @param [in] diff_list             - the list
*/
static LSA_VOID hif_cm_ser_free_diff_list(CM_LIST_TYPED_ENTRY_TYPE(cm_pd_diff_ap_tag) const * const diff_list, HIF_SYS_HANDLE const hSysDev)
{
    LSA_UINT16 				freeRes;
    CM_UPPER_PD_DIFF_AP_PTR_TYPE        diff;
    CM_UPPER_PD_DIFF_MODULE_PTR_TYPE  	module;
    CM_UPPER_PD_DIFF_SUBMODULE_PTR_TYPE submodule;

    while (!CmListIsEmpty(diff_list))
    {
        CmListRemoveHead(diff_list, diff, CM_UPPER_PD_DIFF_AP_PTR_TYPE);
        while (!CmListIsEmpty(&diff->module_list))
        {
            CmListRemoveHead(&diff->module_list, module, CM_UPPER_PD_DIFF_MODULE_PTR_TYPE);
            while (!CmListIsEmpty(&module->submodule_list))
            {
                CmListRemoveHead(&module->submodule_list, submodule, CM_UPPER_PD_DIFF_SUBMODULE_PTR_TYPE);
                freeRes = LSA_RET_ERR_RESOURCE; /* preset negative */
                HIF_FREE_LOCAL_MEMORY(&freeRes, submodule, LSA_COMP_ID_CM, HIF_MEM_TYPE_BUFFER, hSysDev);
                HIF_ASSERT(freeRes == LSA_RET_OK);
            }
            freeRes = LSA_RET_ERR_RESOURCE; /* preset negative */
            HIF_FREE_LOCAL_MEMORY(&freeRes, module, LSA_COMP_ID_CM, HIF_MEM_TYPE_BUFFER, hSysDev);
            HIF_ASSERT(freeRes == LSA_RET_OK);
        }
        freeRes = LSA_RET_ERR_RESOURCE; /* preset negative */
        HIF_FREE_LOCAL_MEMORY(&freeRes, diff, LSA_COMP_ID_CM, HIF_MEM_TYPE_BUFFER, hSysDev);
        HIF_ASSERT(freeRes == LSA_RET_OK);
    }
}



