/**
* \brief used to deserialize the CM diff_list
*
* @param [in] diff_list             - the list
* @param [inout] help_ptr           - helper pointer to the transfer memory SHM
* @param [in] is_upper              - LSA_TRUE: Is HIF Upper instance, else is HIF Lower Instance
* @return help_ptr                  - modified help_ptr
*/
static LSA_UINT8 * hif_cm_deser_diff_list(CM_LIST_TYPED_ENTRY_TYPE(cm_pd_diff_ap_tag) * const diff_list, LSA_UINT8 * help_ptr, LSA_BOOL const is_upper)
{
    LSA_UINT32			 	                             diff_list_count, module_list_count, submodule_list_count;
    LSA_UINT32 					                         i_diff, i_module, i_submodule;
    CM_UPPER_CL_DIFF_AP_PTR_TYPE                         diff;
    CM_UPPER_CL_DIFF_MODULE_PTR_TYPE                     module;
    CM_UPPER_CL_DIFF_SUBMODULE_PTR_TYPE                  submodule;
    CM_LIST_TYPED_ENTRY_TYPE(cm_pd_diff_ap_tag)        * head_diff;
    CM_LIST_TYPED_ENTRY_TYPE(cm_pd_diff_module_tag)    * head_module;
    CM_LIST_TYPED_ENTRY_TYPE(cm_pd_diff_submodule_tag) * head_submodule;

    head_diff = diff_list;
    HIF_SER_COPY_SHMTH_32(&diff_list_count, (LSA_UINT32**)&help_ptr);        /* diff_list_count */
    for (i_diff=diff_list_count; i_diff; i_diff--)
    {
        if (is_upper) /* upper: allocate */
        {
            HIF_ALLOC_LOCAL_MEM((LSA_VOID**)&diff, sizeof(*diff), LSA_COMP_ID_CM, HIF_MEM_TYPE_BUFFER);            /* alloc ap in diff_list */
            HIF_ASSERT(diff);
        }
        else /* lower: take the existing list */
        {
            diff = CmListFirst(diff_list, CM_UPPER_CL_DIFF_AP_PTR_TYPE);
            HIF_ASSERT(diff);
        }

        HIF_SER_COPY_SHMTH_32(&(diff->api),(LSA_UINT32**)&help_ptr);  /* api */
        if (is_upper)
        {
            CmListInitialize(&diff->module_list);
            CmListInsertTail(head_diff, &diff->link);
        }

        head_module = (CM_LIST_ENTRY_TYPE*)&diff->module_list;
        HIF_SER_COPY_SHMTH_32(&module_list_count, (LSA_UINT32**)&help_ptr);           /* module_list_count */
        for (i_module=module_list_count; i_module; i_module--)
        {
            if (is_upper)
            {
                HIF_ALLOC_LOCAL_MEM((LSA_VOID**)&module, sizeof(*module), LSA_COMP_ID_CM, HIF_MEM_TYPE_BUFFER);             /* alloc mod in diff_list */
                HIF_ASSERT(module);
            }
            else
            {
                module = CmListFirst(&diff->module_list, CM_UPPER_CL_DIFF_MODULE_PTR_TYPE);
                HIF_ASSERT(module);
            }
            
            HIF_SER_COPY_SHMTH_16(&module->slot_nr,(LSA_UINT32**)&help_ptr);         /* slot_nr */
            HIF_SER_COPY_SHMTH_32(&module->mod_ident,(LSA_UINT32**)&help_ptr);       /* mod_ident */
            if (is_upper)
            {
              	CmListInitialize(&module->submodule_list);
              	CmListInsertTail(head_module, &module->link);
            }

            head_submodule = (CM_LIST_ENTRY_TYPE*)&module->submodule_list;
            HIF_SER_COPY_SHMTH_32(&submodule_list_count, (LSA_UINT32**)&help_ptr);                       /* submodule_list_count */
            for (i_submodule=submodule_list_count; i_submodule; i_submodule--)
            {
                if (is_upper)
                {
                    HIF_ALLOC_LOCAL_MEM((LSA_VOID**)&submodule, sizeof(*submodule), LSA_COMP_ID_CM, HIF_MEM_TYPE_BUFFER); /* alloc mod in diff_list */
                    HIF_ASSERT(submodule);
                }
                else
                {
                    submodule = CmListFirst(&module->submodule_list, CM_UPPER_CL_DIFF_SUBMODULE_PTR_TYPE);
                    HIF_ASSERT(submodule);
                }

                HIF_SER_COPY_SHMTH_16(&submodule->subslot_nr,(LSA_UINT32**)&help_ptr);      /* subslot_nr */
                HIF_SER_COPY_SHMTH_32(&submodule->sub_ident,(LSA_UINT32**)&help_ptr);       /* sub_ident */
                HIF_SER_COPY_SHMTH_16(&submodule->sub_state,(LSA_UINT32**)&help_ptr);       /* sub_state */
                if (is_upper)
                {
                    CmListInsertTail(head_submodule, &submodule->link);
                }
            }
        }
    }

    return help_ptr;
}



/**
* \brief used to deserialize the CM device_alias_list
*
* @param [in] device_alias_list     - the list
* @param [inout] help_ptr           - helper pointer to the transfer memory SHM
* @param [in] is_upper              - LSA_TRUE: Is HIF Upper instance, else is HIF Lower Instance
* @return help_ptr                  - modified help_ptr
*/
static LSA_UINT8 * hif_cm_deser_device_alias_list(CM_LIST_TYPED_ENTRY_TYPE(cm_arcb_nare_alias_tag) * const device_alias_list, LSA_UINT8 * help_ptr, LSA_BOOL const is_upper)
{
    LSA_UINT32                                         device_alias_list_count;
    LSA_UINT32                                         i_alias;
    CM_UPPER_ARCB_NARE_ALIAS_PTR_TYPE                  alias;
    CM_LIST_TYPED_ENTRY_TYPE(cm_arcb_nare_alias_tag) * head_alias = device_alias_list;

    if /* deserialize upper? */
       (is_upper)
    {
        /* forbidden */
        HIF_FATAL(0);
    }
    else /* deserialize lower */
    {
        HIF_SER_COPY_SHMTH_32(&device_alias_list_count, (LSA_UINT32**)&help_ptr); /* device_alias_list_count */
        for (i_alias=device_alias_list_count; i_alias; i_alias--)
        {
            HIF_ALLOC_LOCAL_MEM((LSA_VOID**)&alias, sizeof(*alias), LSA_COMP_ID_CM, HIF_MEM_TYPE_RQB); /* alloc alias for device_alias_list */
            HIF_ASSERT(alias);
            HIF_SER_COPY_SHMTH_16(&alias->name_length,(LSA_UINT32**)&help_ptr); /* alias->name_length */
            if (HIF_IS_NOT_NULL_U32_REF_BY_PTR(help_ptr) && (alias->name_length > 0)) /* check buffer pointer and length in SHM */
            {
                help_ptr+=HIF_SER_SIZEOF(4); /* skip buffer pointer */
                HIF_ALLOC_LOCAL_MEM((LSA_VOID**)&alias->name, alias->name_length, LSA_COMP_ID_CM, HIF_MEM_TYPE_BUFFER);
                HIF_ASSERT(alias->name);
                HIF_SER_COPY_SHMTH_BUF(alias->name, &help_ptr, alias->name_length); /* name */
            }
            else
            {
                alias->name = LSA_NULL;
                help_ptr+=HIF_SER_SIZEOF(4); /* skip buffer pointer */
            }
            CmListInsertTail(head_alias, &alias->link);
        }
    }

    return help_ptr;
}



