/**
* \brief used to deserialize CM ap_list
*
* @param [in] ap_list		- the list
* @param [inout] help_ptr      	- helper pointer to the transfer memory SHM
* @param [in] is_upper          - LSA_TRUE: HIF Upper instance, else HIF Lower instance
* @return help_ptr            	- modified help_ptr
*/
static LSA_UINT8 * hif_cm_deser_ap_list(CM_LIST_TYPED_ENTRY_TYPE(cm_arcb_ap_tag) * const ap_list, LSA_UINT8 * help_ptr, LSA_BOOL const is_upper)
{
    LSA_UINT32					                      ctr;
    LSA_UINT32 				                          ap_list_count, module_list_count, submodule_list_count, prm_record_list_count;
    LSA_UINT32 				                          i_ap, i_module, i_submodule, i_prm_record;
    CM_UPPER_ARCB_AP_PTR_TYPE                         ap;
    CM_UPPER_ARCB_MODULE_PTR_TYPE           	      module;
    CM_UPPER_ARCB_SUBMODULE_PTR_TYPE        	      submodule;
    CM_UPPER_ARCB_RECORD_PTR_TYPE           	      prm_record;
    CM_LIST_TYPED_ENTRY_TYPE(cm_arcb_ap_tag)        * head_ap;
    CM_LIST_TYPED_ENTRY_TYPE(cm_arcb_module_tag)    * head_module;
    CM_LIST_TYPED_ENTRY_TYPE(cm_arcb_submodule_tag) * head_submodule;
    CM_LIST_TYPED_ENTRY_TYPE(cm_arcb_record_tag)    * head_prm_record;

    if /* deserialize upper? */
       (is_upper)
    {
        /* forbidden */
        HIF_FATAL(0);
    }
    else /* deserialize lower */
    {
        /* AP list */
        /* CmListInitialize() for AP list is already executed */
        head_ap = ap_list;
        HIF_SER_COPY_SHMTH_32(&ap_list_count,(LSA_UINT32**)&help_ptr);                                             	/* ap_list_count */
        for (i_ap=ap_list_count; i_ap; i_ap--)
        {
            HIF_ALLOC_LOCAL_MEM((LSA_VOID**)&ap, sizeof(*ap), LSA_COMP_ID_CM, HIF_MEM_TYPE_RQB);                        /* alloc AP list entry */
            HIF_ASSERT(ap);

            HIF_SER_COPY_SHMTH_32(&ap->api,(LSA_UINT32**)&help_ptr);                                               	/* api */

            /* Module list */
            CmListInitialize(&ap->module_list);
            head_module = (CM_LIST_ENTRY_TYPE*)&ap->module_list;
            HIF_SER_COPY_SHMTH_32(&module_list_count,(LSA_UINT32**)&help_ptr);                                     	/* module_list_count */
            for (i_module=module_list_count; i_module; i_module--)
            {
                HIF_ALLOC_LOCAL_MEM((LSA_VOID**)&module, sizeof(*module), LSA_COMP_ID_CM, HIF_MEM_TYPE_RQB);          	/* alloc Module list entry */
                HIF_ASSERT(module);

                HIF_SER_COPY_SHMTH_16(&module->slot_nr,(LSA_UINT32**)&help_ptr);                                   	/* slot_nr */
                HIF_SER_COPY_SHMTH_16(&module->mod_properties,(LSA_UINT32**)&help_ptr);                            	/* mod_properties */
                HIF_SER_COPY_SHMTH_32(&module->mod_ident,(LSA_UINT32**)&help_ptr);                                 	/* mod_ident */

                /* Submodule list */
                CmListInitialize(&module->submodule_list);
                head_submodule = (CM_LIST_ENTRY_TYPE*)&module->submodule_list;
                HIF_SER_COPY_SHMTH_32(&submodule_list_count,(LSA_UINT32**)&help_ptr);                              	/* submodule_list_count */
                for (i_submodule=submodule_list_count; i_submodule; i_submodule--)
                {
                    HIF_ALLOC_LOCAL_MEM((LSA_VOID**)&submodule, sizeof(*submodule), LSA_COMP_ID_CM, HIF_MEM_TYPE_RQB);	/* alloc Submodule list entry */
                    HIF_ASSERT(submodule);

                    HIF_SER_COPY_SHMTH_16(&submodule->subslot_nr,(LSA_UINT32**)&help_ptr);                         	/* subslot_nr */
                    HIF_SER_COPY_SHMTH_16(&submodule->sub_properties,(LSA_UINT32**)&help_ptr);                     	/* sub_properties */
                    HIF_SER_COPY_SHMTH_32(&submodule->sub_ident,(LSA_UINT32**)&help_ptr);                          	/* sub_ident */

                    /* descr[2] */
                    for (ctr=0; ctr<2UL; ctr++)
                    {
                        HIF_SER_COPY_SHMTH_16(&submodule->descr[ctr].data_description,(LSA_UINT32**)&help_ptr);   	/* data_description */
                        HIF_SER_COPY_SHMTH_16(&submodule->descr[ctr].data_length,(LSA_UINT32**)&help_ptr);           	/* data_length */
                        HIF_SER_COPY_SHMTH_8(&submodule->descr[ctr].iops_length,(LSA_UINT32**)&help_ptr);             	/* iops_length */
                        HIF_SER_COPY_SHMTH_8(&submodule->descr[ctr].iocs_length,(LSA_UINT32**)&help_ptr);             	/* iocs_length */
                    }

                    /* PRM Record list (Attention: union) */
                    CmListInitialize(&submodule->u.cl.prm_record_list);
                    head_prm_record = (CM_LIST_ENTRY_TYPE*)&submodule->u.cl.prm_record_list;
                    HIF_SER_COPY_SHMTH_32(&prm_record_list_count,(LSA_UINT32**)&help_ptr);                      	/* prm_record_list_count */
                    for (i_prm_record=prm_record_list_count; i_prm_record; i_prm_record--)
                    {
                        HIF_ALLOC_LOCAL_MEM((LSA_VOID**)&prm_record, sizeof(*prm_record), LSA_COMP_ID_CM, HIF_MEM_TYPE_RQB); /* alloc PRM Record list entry */
                        HIF_ASSERT(prm_record);

                        HIF_SER_COPY_SHMTH_32(&prm_record->record_index,(LSA_UINT32**)&help_ptr);                     	/* record_index */
                        HIF_SER_COPY_SHMTH_16(&prm_record->transfer_sequence,(LSA_UINT32**)&help_ptr);             	/* transfer_sequence */
                        HIF_SER_COPY_SHMTH_16(&prm_record->record_data_len,(LSA_UINT32**)&help_ptr);               	/* record_data_len */
                        if (HIF_IS_NOT_NULL_U32_REF_BY_PTR(help_ptr) && (prm_record->record_data_len > 0)) /*#22c*/ /* check buffer pointer and length in SHM */
                        {
                            help_ptr+=HIF_SER_SIZEOF(4); /* skip buffer pointer */
                            HIF_ALLOC_LOCAL_MEM((LSA_VOID**)&prm_record->record_data, prm_record->record_data_len, LSA_COMP_ID_CM, HIF_MEM_TYPE_BUFFER);
                            HIF_ASSERT(prm_record->record_data);
                            HIF_SER_COPY_SHMTH_BUF(prm_record->record_data, &help_ptr, prm_record->record_data_len); 	/* record_data */
                        }
                        else
                        {
                            help_ptr+=HIF_SER_SIZEOF(4); /* skip buffer pointer */
                            prm_record->record_data = LSA_NULL;
                        }

                        CmListInsertTail(head_prm_record, &prm_record->link);
                    }
                    /* end of PRM Record list */

                    CmListInsertTail(head_submodule, &submodule->link);
            	}
                /* end of Submodule list */

            	CmListInsertTail(head_module, &module->link);
            }
            /* end of Module list */

            CmListInsertTail(head_ap, &ap->link);
    	}
        /* end of AP list */
    }

    return help_ptr;
}



