static LSA_UINT8 * hif_cm_deser_ir_info_sub_list(CM_LIST_TYPED_ENTRY_TYPE(cm_arcb_ir_info_sub_tag) * const ir_info_sub_list, LSA_UINT8 * help_ptr, LSA_BOOL const is_upper)
{
    LSA_UINT32 				           	                ir_info_sub_list_count;
    LSA_UINT32 				           	                i_ir_info_sub;
    CM_UPPER_ARCB_IR_INFO_SUB_PTR_TYPE      		    ir_info_sub;
    CM_LIST_TYPED_ENTRY_TYPE(cm_arcb_ir_info_sub_tag) * head_ir_info_sub;

    if /* deserialize upper? */
       (is_upper)
    {
        /* forbidden */
        HIF_FATAL(0);
    }
    else /* deserialize lower */
    {
        /* IR info sub list */
        /* CmListInitialize() for IR info sub list is already executed */
        head_ir_info_sub = ir_info_sub_list;
        HIF_SER_COPY_SHMTH_32(&ir_info_sub_list_count,(LSA_UINT32**)&help_ptr);                     	/* ir_info_sub_list_count */
        for (i_ir_info_sub=ir_info_sub_list_count; i_ir_info_sub; i_ir_info_sub--)
        {
            HIF_ALLOC_LOCAL_MEM((LSA_VOID**)&ir_info_sub, sizeof(*ir_info_sub), LSA_COMP_ID_CM, HIF_MEM_TYPE_RQB); /* alloc IR info sub list entry */
            HIF_ASSERT(ir_info_sub);

            HIF_SER_COPY_SHMTH_16(&ir_info_sub->iocr_ref,(LSA_UINT32**)&help_ptr);      		/* iocr_ref */
            HIF_SER_COPY_SHMTH_16(&ir_info_sub->subframe_offset,(LSA_UINT32**)&help_ptr);               /* subframe_offset */
            HIF_SER_COPY_SHMTH_32(&ir_info_sub->subframe_data,(LSA_UINT32**)&help_ptr);                 /* subframe_data */

            CmListInsertTail(head_ir_info_sub, &ir_info_sub->link);
        }
        /* end of IR info sub list */
    }

    return help_ptr;
}




